import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "./Login.css";
import coffeeCup from "../assets/login-cup.jpeg";

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', { email, password });
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard'); // Assuming you have a dashboard page
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <div className="outer-container">
      <div className="container">
        <div className="left-container">
          <img className="loginImg" src={coffeeCup} alt="Cup of Coffee" />
        </div>
        <div className="right-outer-container">
          <div className="right-inner-container">
            <div className="top-details">
              <p className="heading">Welcome Back</p>
              <p className="desc">Enter your account credentials to order!</p>
            </div>

            <form className="form" onSubmit={handleSubmit}>
              <div className="details-container">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  required
                  className="form-input"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="details-container">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  required
                  className="form-input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <p className="forgot-password">Forgot your Password?</p>
              <button type="submit" className="btn login-btn">Login</button>
            </form>
            <p >Don't have an account?</p> <a href="/signup"> Sign up</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
